    private void initiateAccounts(){
        ArrayList<Accounts> accounts = Utility.readFile("accounts.txt");
        
        for(Accounts a : accounts){
            numberDropdown.addItem(a);
        }
    }

    public CRMapp() {
        initComponents();
        initiateAccounts();
    }

