    private void numberDropdownActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_numberDropdownActionPerformed
        Accounts currentAccount = (Accounts) numberDropdown.getSelectedItem();
        nameField.setText(currentAccount.getAccountName());
        adressField.setText(currentAccount.getAccountAdress());
        balanceField.setText(String.valueOf(currentAccount.getAccountBalance()));
    }//GEN-LAST:event_numberDropdownActionPerformed

